sys_script_include.script:
var FinesseCredentials = Class.create();
FinesseCredentials.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCredentials:function(){
		var json = new global.JSON();
		var sys_id = gs.getUserID();

		var gr = new GlideRecord('x_241495_acqcti_finesse_configuration');
		gr.addQuery("sn_user",sys_id);
		gr.query();

		if(gr.next()){
			var result = {
				"username" : gr.getValue("finesse_username"),
				"password" : gr.finesse_password.getDecryptedValue(),
				"extension" : gr.getValue("finesse_extension"),
				"buid" : gr.getValue("business_unit_id")
			};
			return json.encode(result);
		}
	},

	saveCredentials:function(){
		var sys_id = gs.getUserID();

		var username = this.getParameter('sysparm_username');
		var password = this.getParameter('sysparm_password');
		var stationPhoneNumber = this.getParameter('sysparm_stationPhoneNumber');
		var businessUnitId = this.getParameter('sysparm_businessUnitId');

		var gr = new GlideRecord('x_241495_acqcti_finesse_configuration');
		gr.addQuery("sn_user",sys_id);
		gr.query();
		var count = gr.getRowCount();
		if (count > 0) {
		   while (gr.next()) {
			  gr.finesse_username = username;
			   gr.finesse_password = password;
			   gr.finesse_extension = stationPhoneNumber;
			   gr.business_unit_id = businessUnitId;
			  gr.update();
			   return "User credentials updated";
		   }
		}else{
			gr = new GlideRecord('x_241495_acqcti_finesse_configuration');
			gr.initialize(); 
			gr.finesse_username = username;
			gr.finesse_password = password;
			gr.finesse_extension = stationPhoneNumber;
			gr.business_unit_id = businessUnitId;
			gr.sn_user = sys_id;
			gr.insert();
			return "User credentials inserted";
		}
	},
    type: 'FinesseCredentials'
});
